package com.kaizen.operators;

public class Arithmaticoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 5;
		System.out.println("addition of a&b " + (a+b));
		System.out.println("subtraction of a&b " + (a-b));
		System.out.println("multiplication of a&b " + (a*b));
		System.out.println("division of a&b " + (a/b));
		System.out.println("modulus of a&b " + (a%b));
		System.out.println("increment of a" + (++a));
		System.out.println(a);
		
		System.out.println("decrement of a " + (--a));
		System.out.println("Addition assignment  " + (a += b));
		System.out.println("subtraction assignment  " + (a -= b));
		System.out.println("multiplication assignment  " + (a *= b));
		System.out.println("division assignment  " + (a /= b));
		//int minute = 59;
		//System.out.println("fraction of the hour that passed: ");
		//System.out.println(minute/60.0);
		//System.out.println(0.0/0);
		//System.out.println(0/0);
		

	}

}
