package com.kaizen.operators;

public class Mathfunction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x = 6;
		int y = 7;
		int a =45;
		int b = 90;
		
		
		
		System.out.println(Math.sqrt(x));
		System.out.println(Math.sqrt(y));
		System.out.println(Math.pow(x, 5));
		System.out.println(Math.pow(y, x));
		System.out.println(Math.sin(a));
		System.out.println(Math.sin(b));
		System.out.println(Math.cos(a));
		System.out.println(Math.tan(b));
		System.out.println(Math.atan(a));
		System.out.println(Math.atan(b));
		System.out.println(Math.atan2(a, b));

	}

}
