package com.kaizen.operators;

public class ternaryoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		a = 17;
		b=(a>=15)?64:58;
		System.out.println(b);
		b=(a<=18)?64:58;
		System.out.println(b);
		b=(a==17)?64:58;
		System.out.println(b);
	}
		

	}

